    }
    const openRegister=()=>{
        setIsShown(true)
        setRegister(!register)
        setLogin(false)
    }
    const closeModal = ()=>{
        setRegister(false)
        setLogin(false)
        setIsShown(false)
    }
    return(
        <ModalContext.Provider value={{isShown,login,register,setIsShown,setLogin,setRegister}}>
            {children}
        </ModalContext.Provider>
    )
}
