import { takeEvery, call, put, take } from "redux-saga/effects";
import { actions } from "./actions";
import axios from "axios";
import { useNavigate } from "react-router-dom";
import Cookies from "js-cookie";

function* getPosts({ payload }) {
    try {
      const {tag} = payload
      const response = yield call(async () => {
        return await axios
          .get(`http://localhost:3009/api/v1/post/${tag}`)
          .then((res) => {
            return res;
          });
      });
      yield put(actions.getPostsSuccess(response.data.data));
    //   payload.onLoginSuccess()
    } catch (error) {
      console.log(error)
    //   yield put(actions.loginFailed(error?.response?.data?.message));
    }
}


export function* postSaga() {
    yield takeEvery(actions.getPosts, getPosts);
}
