import React from "react";
import { call } from "redux-saga/effects";
import socketio from "socket.io-client";
export const ModalContext = React.createContext(false);
export const ModalProvider = ({children})=>{
    const [isShown,setIsShown] = React.useState(false)
    const [login,setLogin] = React.useState(false)
    const [register,setRegister] = React.useState(false)
    function openLogin(){
        setRegister(false)
        setLogin(true)
        setIsShown(true)
    }
    function openRegister(){
        setRegister(true)
        setLogin(false)
        setIsShown(true)
    }
    function close(){
        setRegister(false)
        setLogin(false)
        setIsShown(false)
    }
    return(
        <ModalContext.Provider value={{isShown,login,register,setIsShown,setLogin,setRegister,openLogin,close,openRegister}}>
            {children}
        </ModalContext.Provider>
    )
}

export const socket = socketio.connect(`http://localhost:3009`, {
    withCredentials: true,
});
function Socket(socket){
    return new function(socket){
        this.socket = socket
        this.like = async function(id,callback){
            await socket.emit('like',id)
            callback()
        }
    }
}
const listener = Socket(socket)
export const SocketContext = React.createContext(false);

export const SocketProvider = ({children})=>{
    return(
        <SocketContext.Provider value={{socket,like:listener.like}}>
            {children}
        </SocketContext.Provider>
    )
}
